import { approveABI } from "../../helpers/helper";
// Connect Wallet
import Web3 from 'web3';
import WalletConnect from '@walletconnect/client';
import QRCodeModal from '@walletconnect/qrcode-modal';
import * as i0 from "@angular/core";
import * as i1 from "../../services/contract.service";
import * as i2 from "../../services/storage.service";
import * as i3 from "@angular/router";
import * as i4 from "@angular/common";
const _c0 = function (a0, a1) { return { "hidden": a0, "block": a1 }; };
// Set web3 and connector
const web3 = new Web3(window['ethereum']);
const connector = new WalletConnect({
    bridge: 'https://bridge.walletconnect.org',
});
export class NavbarComponent {
    constructor(contractService, storageService, router) {
        this.contractService = contractService;
        this.storageService = storageService;
        this.router = router;
        this.account = {};
        this.approveAddress = "0x2811dE52B41267D6FD126B4F8d0ac2248E1C9624";
        this.userBalance = '';
        this.showMobileMenu = false;
        // Meta mask connection
        this.openMetamask = async () => {
            this.ethereum = window['ethereum'];
            if (typeof this.ethereum !== 'undefined') {
            }
            const accounts = await this.ethereum.request({
                method: 'eth_requestAccounts',
            });
            this.setAccount(accounts[0], this.ethereum.chainId, 'metamask');
            this.metamastListener();
            // window.location.reload();
        };
        this.connectWallet = async () => {
            // Create a connector
            this.connector = new WalletConnect({
                bridge: 'https://bridge.walletconnect.org',
                qrcodeModal: QRCodeModal,
            });
            // Check if connection is already established
            if (!this.connector.connected) {
                // create new session
                this.connector.createSession();
            }
            this.wallectConnectListener();
        };
    }
    toggleNavbar() {
        this.showMobileMenu = !this.showMobileMenu;
    }
    ngOnInit() {
        this.account =
            this.storageService.getItem('account') === null
                ? { address: '', network: '', chainId: '', provider: '' }
                : JSON.parse(this.storageService.getItem('account') || '{}');
        this.account.address = "ConnectWallet";
        this.updateBalance();
    }
    async updateBalance() {
        this.approveContract = await new web3.eth.Contract(approveABI, this.approveAddress);
        let approveToken = await this.approveContract.methods
            .balanceOf(localStorage.getItem("connecttedAddress"))
            .call()
            .then((receipt) => {
            this.userBalance = receipt;
            console.log(receipt / 10 ** 18);
        });
    }
    metamastListener() {
        // Listener
        this.ethereum.on('accountsChanged', (accounts) => {
            this.setAccount(accounts[0], this.ethereum.chainId, 'metamask');
        });
        this.ethereum.on('chainChanged', (chainId) => {
            this.setAccount(this.account.address, chainId, 'metamask');
        });
        this.storageService.setItem('walletconnect', '');
    }
    wallectConnectListener() {
        // Subscribe to connection events
        this.connector.on('connect', (error, payload) => {
            // window.location.reload();
            if (error) {
                throw error;
            }
            // Get provided accounts and chainId
            const { accounts, chainId } = payload.params[0];
            this.setAccount(accounts[0], chainId, 'trustwallet');
        });
        this.connector.on('session_update', (error, payload) => {
            if (error) {
                throw error;
            }
            // Get updated accounts and chainId
            const { accounts, chainId } = payload.params[0];
            this.setAccount(accounts[0], chainId, 'trustwallet');
        });
        this.connector.on('disconnect', (error, payload) => {
            if (error) {
                throw error;
            }
            // Delete connector
            this.setAccount(' ', ' ', ' ');
        });
    }
    async setAccount(address, chainId, provider) {
        let account;
        if (address != '' && address != undefined) {
            const { network, key } = await this.setNetwork(chainId);
            account = {
                address: address,
                chainId: chainId,
                network,
                key,
                provider: provider,
            };
        }
        else {
            account = {
                address: '',
                network: '',
                chainId: '',
                provider: '',
                key: '',
            };
        }
        this.contractService.setAccount(account);
        this.account = Object.assign({}, account);
        this.storageService.setItem('account', JSON.stringify(this.account));
        this.storageService.setItem('connecttedAddress', this.account.address);
    }
    setNetwork(chainId) {
        let network;
        let key;
        switch (chainId) {
            case '0x1':
            case 1:
                network = 'Mainnet';
                key = 'ETH';
                break;
            case '0x3':
            case 3:
                network = 'Ropsten';
                key = 'ETH';
                break;
            case '0x4':
            case 4:
                network = 'Rinkeby';
                key = 'ETH';
                break;
            case '0x38':
            case 56:
                network = 'BSC Mainnet';
                key = 'BSC';
                break;
            case '0x61':
            case 97:
                network = 'BSC Testnet';
                key = 'BSC';
                break;
            default:
                network = 'Unknown';
                break;
        }
        return { network, key };
    }
}
